#809下发路线信息记录表
DROP TABLE IF EXISTS `zw_m_809_line_record`;
CREATE TABLE `zw_m_809_line_record` (
  `id` varchar(64) NOT NULL,
  `vehicle_id` varchar(64) NOT NULL COMMENT '车id',
  `brand` varchar(10) NOT NULL COMMENT '车牌',
  `vehicle_color` smallint(6) DEFAULT NULL COMMENT '车辆颜色',
  `line_id` varchar(64) DEFAULT NULL COMMENT '线路id',
  `line_uuid` varchar(64) DEFAULT NULL COMMENT '线路uuid',
  `fence_config_id` varchar(64) NOT NULL COMMENT '风险围栏id',
  `receive_time` datetime DEFAULT NULL COMMENT '收到上级平台的路线信息时间',
  `swift_number` int(11) DEFAULT NULL COMMENT '流水号',
  `flag` smallint(6) DEFAULT NULL COMMENT '逻辑删除标记',
   primary key(`id`),
   KEY `idx_vid_time` (`vehicle_id`,`receive_time`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='809下发路线信息记录表';


