mapper.select:
select peo.id,peo.people_number `name`,peo.`name` alias,peo.org_id,'1' as monitorType, case WHEN cofig.vehicle_id<>'' then 1 else 0 end as bindType,peo.remark, cofig.id configId, cofig.device_id,device.device_number, cofig.sim_card_id,simcard.simcard_number simCardNumber, simcard.real_id realSimCardNumber,device.device_type,device.functional_type, device.terminal_type_id, terminal.terminal_type,terminal.terminal_manufacturer,GROUP_CONCAT(DISTINCT assign.assignment_id) groupId, DATE_FORMAT(cofig.create_data_time,'%Y-%m-%d') AS bindDate, DATE_FORMAT(cofig.update_data_time,'%Y-%m-%d') AS updateBindDate, DATE_FORMAT(service.billing_date,'%Y-%m-%d') AS billing_date, DATE_FORMAT(service.expire_date,'%Y-%m-%d') AS expire_date, intercom.intercom_device_id intercomDeviceNumber, intercom.user_id,intercom.number callNumber, peo.gender,peo.birthday,peo.identity,peo.phone,peo.address,peo.email,peo.identity_card_photo, peo.is_incumbency,peo.job_id,job.job_name,peo.nation_id,peo.blood_type_id,peo.qualification_id, GROUP_CONCAT(DISTINCT base.basic_id) skillIds,GROUP_CONCAT(DISTINCT skill.name) skillNames, GROUP_CONCAT(DISTINCT driver.basic_id) driverTypeIds,GROUP_CONCAT(DISTINCT dc.value) driverTypeNames,simcard.auth_code from zw_m_people_info peo LEFT JOIN zw_m_config cofig on peo.id = cofig.vehicle_id and cofig.flag = 1 LEFT JOIN zw_m_device_info device on cofig.device_id = device.id LEFT JOIN zw_m_sim_card_info simcard on cofig.sim_card_id = simcard.id LEFT JOIN zw_m_terminal_type terminal on device.terminal_type_id = terminal.id and terminal.flag=1 LEFT JOIN zw_m_assignment_vehicle assign on peo.id = assign.vehicle_id and assign.flag = 1 LEFT JOIN zw_m_service_lifecycle service on cofig.service_lifecycle_id = service.id LEFT JOIN zw_m_intercom_info intercom on cofig.intercom_info_id = intercom.id and intercom.flag =1 LEFT JOIN zw_m_job_info job on peo.job_id = job.id LEFT JOIN zw_m_people_basic_info base on peo.id = base.people_id and base.flag = 1 and base.type = 1 LEFT JOIN zw_m_people_basic_info driver on peo.id = driver.people_id and driver.flag = 1 and driver.type = 2 LEFT JOIN zw_m_skill skill on base.basic_id = skill.id left Join zw_c_dictionary dc on driver.basic_id = dc.id where peo.flag = 1 and peo.id IN
#{item}
GROUP BY peo.id

SELECT p.id,p.`name` as alias,p.org_id,p.people_number as `name`,case when c.vehicle_id <> '' then '1' else '0' end as bindType,case when c.intercom_info_id <> '' then '1' else '0' end as intercomBindType from zw_m_people_info p left join zw_m_config c ON p.id = c.vehicle_id and c.flag = 1 where p.flag = 1
and p.people_number in
#{peopleNum}

