#平台巡检记录表
CREATE TABLE `zw_m_platform_inspection` (
  `id` varchar(64) NOT NULL,
  `vehicle_id` varchar(64) NOT NULL COMMENT '车id',
  `inspection_type` smallint(6) DEFAULT NULL COMMENT '巡检的类型（1.车辆运行监测巡检2.驾驶员驾驶行为监测巡检 3.驾驶员身份识别巡检)',
  `inspector` varchar(50) DEFAULT NULL COMMENT '巡检人',
  `inspection_time` datetime DEFAULT NULL COMMENT '巡检时间',
  `inspection_status` smallint(6) DEFAULT NULL COMMENT '巡检状态(1下发中，2下发成功，3终端响应超时，4终端离线 )',
  `inspection_result_id` varchar(64) DEFAULT NULL COMMENT '巡检结果的表的id',
  `flag` smallint(6) DEFAULT NULL COMMENT '逻辑删除标记',
  KEY `idx_vid_time_type` (`vehicle_id`,`inspection_time`,`inspection_type`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='巡检记录表';

#巡检结果表
CREATE TABLE `zw_m_platform_inspection_result` (
  `id` varchar(64) NOT NULL,
  `vehicle_id` varchar(64) NOT NULL COMMENT '车id',
  `image_url` varchar(360) DEFAULT NULL COMMENT '图片附件地址',
  `video_url` varchar(360) DEFAULT NULL COMMENT '视频附件地址',
  `time` datetime DEFAULT NULL COMMENT '时间',
  `alarm_type` smallint(6) DEFAULT NULL COMMENT '报警类型',
  `warn_type` smallint(6) DEFAULT NULL COMMENT '预警类型',
  `remind_flag` smallint(6) DEFAULT NULL COMMENT '是否有效提醒驾驶员',
   `type` smallint(6) DEFAULT NULL COMMENT '道路偏离类型（1 左侧偏离 2右侧偏离）禁行类型(1 禁行区域， 2禁行)',
  `status` smallint(6) DEFAULT NULL COMMENT '标志（1开始标志 2持续标志 3 结束标志）',
  `route_id` varchar(64) DEFAULT NULL COMMENT '线路/区域 id',
  `driver_id` varchar(64) DEFAULT NULL COMMENT '驾驶员id',
  `inspection_type` smallint(6) DEFAULT NULL COMMENT '巡检类型（1.车辆运行监测巡检2.驾驶员驾驶行为监测巡检）'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='巡检结果表';

